import React, { useState, useEffect } from 'react';

// Helper function to flatten JSON structure
const flattenJson = (data, parentKey = '') => {
  let flattened = {};
  Object.keys(data).forEach(key => {
    const newKey = parentKey ? `${parentKey}.${key}` : key;
    if (Array.isArray(data[key])) {
      flattened[newKey] = data[key];
    } else if (typeof data[key] === 'object' && !Array.isArray(data[key])) {
      Object.assign(flattened, flattenJson(data[key], newKey));
    } else {
      flattened[newKey] = data[key];
    }
  });
  return flattened;
};

// Helper function to unflatten JSON structure
const unflattenJson = (data) => {
  const result = {};
  Object.keys(data).forEach((key) => {
    const keys = key.split('.');
    keys.reduce((acc, value, index) => {
      if (index === keys.length - 1) {
        acc[value] = data[key];
      } else {
        if (!acc[value]) {
          acc[value] = {};
        }
      }
      return acc[value];
    }, result);
  });
  return result;
};

const QuestionForm = ({ initialJson, validatorFile }) => {
  const [formData, setFormData] = useState({});
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [error, setError] = useState("");
  const [listItemCount, setListItemCount] = useState(null);
  const [listIndex, setListIndex] = useState(0);

  // Load the JSON file, flatten it, and frame questions using the validator
  useEffect(() => {
    if (initialJson && validatorFile) {
      // Flatten the initial JSON structure
      const flattenedJson = flattenJson(initialJson);
      setFormData(flattenedJson);

      // Generate questions from the validatorFile's 'query' field
      const generatedQuestions = Object.keys(flattenedJson).map(key => ({
        key,
        query: validatorFile[key]?.query || key,
        typeof: validatorFile[key]?.typeof || 'string'
      }));
      setQuestions(generatedQuestions);
    }
  }, [initialJson, validatorFile]);

  // Function to validate the answer
  const validateAnswer = (answer, type) => {
    switch (type) {
      case 'string':
        return typeof answer === 'string';
      case 'number':
        return !isNaN(answer);
      case 'boolean':
        return answer.toLowerCase() === 'true' || answer.toLowerCase() === 'false';
      case 'object':
        return Number.isInteger(Number(answer)) && Number(answer) > 0;
      default:
        return false;
    }
  };

  // Function to handle form submission and move to the next question
  const handleSubmit = (event) => {
    event.preventDefault();
    const input = event.target.elements.questionInput.value;
    const currentType = questions[currentQuestion].typeof;
    const currentKey = questions[currentQuestion].key;

    // If we are asking for the number of objects in a list
    if (currentType === 'object' && !listItemCount) {
      const isValid = validateAnswer(input, 'object');
      if (!isValid) {
        setError(`Invalid input! Please enter a valid number.`);
        return;
      }
      setError("");
      setListItemCount(Number(input));
      setListIndex(0); // Start asking for the list items
    }
    // If we are asking for the fields of the objects in the list
    else if (listItemCount && listIndex < listItemCount) {
      const listKey = `${currentKey}[${listIndex}]`;
      const isValid = validateAnswer(input, currentType);
      if (!isValid) {
        setError(`Invalid input! Expected a ${currentType}.`);
        return;
      }
      setError("");
      setFormData({
        ...formData,
        [listKey]: input
      });

      if (listIndex + 1 < listItemCount) {
        setListIndex(listIndex + 1);
      } else {
        setListItemCount(null);
        setListIndex(0);
        setCurrentQuestion(currentQuestion + 1);
      }
    } else {
      // Validate the input based on the 'typeof' field
      if (!validateAnswer(input, currentType)) {
        setError(`Invalid input! Expected a ${currentType}.`);
        return;
      }
      setError("");
      // Update the form data
      setFormData({
        ...formData,
        [currentKey]: input
      });
      // Move to the next question
      setCurrentQuestion(currentQuestion + 1);
    }
  };

  return (
    <div>
      {currentQuestion < questions.length ? (
        <form onSubmit={handleSubmit}>
          <label>
            {listItemCount
              ? `What is ${questions[currentQuestion].query} for item ${listIndex + 1}?`
              : `What is ${questions[currentQuestion].query}?`}
          </label>
          <input
            type="text"
            name="questionInput"
            required
          />
          <button type="submit">Next</button>
          {error && <p style={{ color: 'red' }}>{error}</p>}
        </form>
      ) : (
        <div>
          <h3>Thank you! Here is the filled JSON:</h3>
          <pre>{JSON.stringify(unflattenJson(formData), null, 2)}</pre>
        </div>
      )}
    </div>
  );
};

// Usage of QuestionForm with nested JSON and the validator file
const App = () => {
  const initialJson = {
    one: "",
    two: "",
    three: {
      "t-one": "",
      "t-two": ""
    },
    four: [
      {
        "f-one": "",
        "f-two": ""
      }
    ]
  };

  const validatorFile = {
    one: {
      typeof: "string",
      query: "value of one"
    },
    two: {
      typeof: "string",
      query: "value of two"
    },
    "t-one": {
      typeof: "string",
      query: "value of t-one"
    },
    "t-two": {
      typeof: "string",
      query: "value of t-two"
    },
    four: {
      typeof: "object",
      query: "number of fours"
    },
    "f-one": {
      typeof: "string",
      query: "value of f-one"
    },
    "f-two": {
      typeof: "string",
      query: "value of f-two"
    }
  };

  return (
    <div>
      <h1>Dynamic JSON Validator Questionnaire</h1>
      <QuestionForm initialJson={initialJson} validatorFile={validatorFile} />
    </div>
  );
};

export default App;
